export class TaskDetailComponent {
    constructor(taskService, route) {
        this.taskService = taskService;
        this.route = route;
        this.taskForm = new FormGroup({
            // title: new FormControl(),
            // assignedTo: new FormControl(),
            priority: new FormControl(),
            // startDate: new FormControl(),
            // endDate: new FormControl(),
            // cost: new FormControl(),
            // costInDays: new FormControl(),
        });
    }
    ngOnInit() {
        this.route.paramMap.subscribe((data) => {
            this.id = data.params["id"];
            this.getTask(data.params["id"]);
        });
        this.init();
    }
    getTask(taskId) {
        this.task = this.taskService.getTask(taskId);
    }
    init() {
        this.chart = new Chart({
            chart: {
                type: 'line'
            },
            title: {
                text: 'Linechart'
            },
            credits: {
                enabled: false
            },
            series: [{
                    type: 'line',
                    name: 'Line 1',
                    data: [1, 2, 3]
                }]
        });
        this.chart.addPoint(4);
        this.chart.addPoint(5);
        setTimeout(() => {
            this.chart.addPoint(6);
        }, 2000);
        this.chart.ref$.subscribe(console.log);
    }
    approve(project) {
        project["status"] = "OPEN";
        this.taskService.updateProject(project).subscribe();
    }
    decline(project) {
        project["status"] = "DEFERRED";
        this.taskService.updateProject(project).subscribe();
    }
    pull() {
        this.getTask(this.id);
    }
    update(newTask) {
        console.log(this.taskForm.value);
        // newTask["title"] = this.taskForm.get("title")?.valueChanges
        // newTask["assignedTo"] = this.taskForm.get("assignedTo")?.valueChanges
        // newTask["priority"] = this.taskForm.get("priority")?.valueChanges
        // newTask["cost"] = this.taskForm.get("cost")?.valueChanges
        // newTask["costInDays"] = this.taskForm.get("costInDays")?.valueChanges
        newTask["priority"] = this.taskForm.get("priority");
        this.taskService.updateProject(newTask).subscribe();
    }
}
