export class TaskDetailComponent {
  constructor(taskService, route, employeeService) {
    this.taskService = taskService;
    this.route = route;
    this.employeeService = employeeService;
    this.taskForm = new FormGroup({
      title: new FormControl("Task Name"),
      // projectName: new FormControl(Validators.required),
      description: new FormControl(),
      assignedTo: new FormControl(),
      startDate: new FormControl("2021-07-21"),
      endDate: new FormControl("2021-07-21"),
      priority: new FormControl("HIGH"),
      // status: new FormControl("WAITING"),
      // percentComplete: new FormControl(0),
      cost: new FormControl(200),
      costInDays: new FormControl(10)
    });
    this.taskCreateForm = new FormGroup({
      title: new FormControl("Task Name"),
      // project: new FormControl(Validators.required),
      // description: new FormControl("Insuarance"),
      assignedTo: new FormControl(),
      startDate: new FormControl("2021-07-21"),
      endDate: new FormControl("2021-07-21"),
      priority: new FormControl("HIGH"),
      status: new FormControl("WAITING"),
      // percentComplete: new FormControl(0),
      cost: new FormControl(200),
      costInDays: new FormControl(10)
    });
    this.modalCreateClass = "modal";
    this.modalCreateSubTaskClass = "modal";
    this.resolvable = true;
  }

  ngOnInit() {
    this.route.paramMap.subscribe(data => {
      this.id = data.params["id"];
      this.getTask(data.params["id"]);
      this.subTasks = this.taskService.findByParent(data.params["id"]);
      this.resolvable = true;
      this.subTasks.subscribe(data => {
        this.resolvable = true;

        for (let subTask of data._embedded.tasks) {
          if (subTask.status == 'INPROGRESS' || subTask.status == 'WAITING') {
            this.resolvable = false;
          }
        }
      });
      this.init();
      this.getEmpolyees();
    });
  }

  getTask(taskId) {
    this.task = this.taskService.getTask(taskId);
    this.task.subscribe(data => {
      this.projectId = data.project;
    });
  }

  init() {
    var members = {};
    var ids = [];
    this.subTasks.subscribe(subTasksData => {
      console.log(subTasksData);

      for (var i = 0; i < subTasksData._embedded.tasks.length; i++) {
        let subTask = subTasksData._embedded.tasks[i]; // console.log(taskData)

        if (subTask.status == 'COMPLETED') {
          if (members[subTask.assignedTo]) {
            members[subTask.assignedTo]['total'] = members[subTask.assignedTo]['total'] + 1;
            members[subTask.assignedTo]['done'] = members[subTask.assignedTo]['done'] + 1;
          } else {
            members[subTask.assignedTo] = {
              total: 1,
              done: 1,
              reject: 0
            };
            ids.push(subTask.assignedTo);
          }
        } else {
          if (subTask.status == 'DEFERRED') {
            if (members[subTask.assignedTo]) {
              members[subTask.assignedTo]['total'] = members[subTask.assignedTo]['total'] + 1;
              members[subTask.assignedTo]['reject'] = members[subTask.assignedTo]['reject'] + 1;
            } else {
              members[subTask.assignedTo] = {
                total: 1,
                reject: 1,
                done: 0
              };
              ids.push(subTask.assignedTo);
            }
          } else {
            if (members[subTask.assignedTo]) {
              members[subTask.assignedTo]['total'] = members[subTask.assignedTo]['total'] + 1;
            } else {
              members[subTask.assignedTo] = {
                total: 1,
                done: 0,
                reject: 0
              };
              ids.push(subTask.assignedTo);
            }
          }
        }
      } // console.log(members)


      var total = {
        // type: 'bar',
        name: 'Total',
        data: []
      };
      var done = {
        // type: 'bar',
        name: 'Done',
        data: []
      };
      var inprogress = {
        // type: 'bar',
        name: 'In Progress',
        data: []
      };
      var reject = {
        // type: 'bar',
        name: 'Reject',
        data: []
      };
      var donePie = ["Done", 0];
      var inprogressPie = ["In Progress", 0];
      var rejectPie = ["Reject", 0]; // console.log(ids)

      var categories = [];

      for (let id of ids) {
        let member = members[id];
        total.data.push(member.total);
        done.data.push(member.done);
        reject.data.push(member.reject);
        inprogress.data.push(member.total - member.done - member.reject);
        donePie[1] = donePie[1] + member.done;
        rejectPie[1] = rejectPie[1] + member.reject;
        inprogressPie[1] = inprogressPie[1] + (member.total - member.done - member.reject); // rejectPie.push(member.reject)
        // donePie.push(member.done)
        // inprogressPie.push(member.total - member.done - member.reject)

        if (id == 0) {
          categories.push('-');
        } else {
          categories.push(id);
        } // console.log(categories)

      } // console.log(totalBar.data)
      // total.type = "bar"
      // done.type = "bar"
      // inprogress.type = "bar"


      this.barChart = new Chart({
        chart: {
          type: 'bar'
        },
        colors: ['rgb(124, 181, 236)', '#fe6694', 'rgb(144, 237, 125)', '#f77f00'],
        title: {
          text: 'barchart'
        },
        credits: {
          enabled: false
        },
        xAxis: {
          categories: categories,
          title: {
            text: 'employees'
          }
        },
        series: [total, done, inprogress, reject]
      }); // total.type = "pie"
      // done.type = "pie"
      // inprogress.type = "pie"
      // console.log(done[1])

      this.pieChart = new Chart({
        chart: {
          type: 'pie',
          plotShadow: false,
          backgroundColor: 'transparent',
          height: '300px' // width: '100px'

        },
        colors: ['rgb(124, 181, 236)', '#fe6694', 'rgb(144, 237, 125)'],
        title: {
          text: 'piechart',
          x: 35,
          align: 'left'
        },
        credits: {
          enabled: false
        },
        plotOptions: {
          pie: {
            allowPointSelect: true,
            cursor: 'pointer',
            dataLabels: {
              enabled: false
            },
            showInLegend: true,
            shadow: false,
            center: ['20%', '30%'],
            size: '80%',
            innerSize: '10%'
          }
        },
        legend: {
          layout: 'vertical',
          // backgroundColor: '#transparent',
          floating: true,
          // align: 'left',
          x: 90,
          verticalAlign: 'top',
          y: 70,
          itemMarginTop: 10,
          itemMarginBottom: 5
        },
        // xAxis: {
        //   categories: categories, title: {
        //     text: 'employees'
        //   }},
        series: [{
          type: 'pie',
          name: 'Tasks',
          // data: [total.data - inprogress.data - reject.data, inprogress.data, reject.data]
          // data: [['Total',total.data - inprogress.data - reject.data], ['In Progress',inprogress.data], ['Reject',reject.data]]
          data: [inprogressPie, donePie, rejectPie]
        }]
      });
      this.pieChart.ref$.subscribe(console.log);
      this.barChart.ref$.subscribe(console.log);
    });
  }
  /* Chart */


  approve(task) {
    task["status"] = "INPROGRESS";
    var startDate = new Date();
    task["startDate"] = `${startDate.getFullYear()}-${startDate.getMonth() + 1 > 9 ? startDate.getMonth() + 1 : '0' + (startDate.getMonth() + 1)}-${startDate.getDate() > 9 ? startDate.getDate() : '0' + startDate.getDate()}`;
    this.taskService.updateTask(task).subscribe();
  }

  decline(task) {
    task["status"] = "DEFERRED";
    this.taskService.updateTask(task).subscribe();
  }

  resolve(task) {
    task["status"] = "COMPLETED";
    var endDate = new Date();
